import React from "react";

// reactstrap components
import { Container, UncontrolledDropdown, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from "reactstrap";

// core components

function ProfilePageHeader() {
  let pageHeader = React.createRef();

  React.useEffect(() => {
    if (window.innerWidth > 991) {
      const updateScroll = () => {
        let windowScrollTop = window.pageYOffset / 3;
        pageHeader.current.style.transform =
          "translate3d(0," + windowScrollTop + "px,0)";
      };
      window.addEventListener("scroll", updateScroll);
      return function cleanup() {
        window.removeEventListener("scroll", updateScroll);
      };
    }
  });
  return (
    <>
      <div
        className="page-header clear-filter page-header-small"
        filter-color="blue"
      >
        <div
          className="page-header-image"
          style={{
            backgroundImage: "url(" + require("assets/img/bg5.jpg") + ")"
          }}
          ref={pageHeader}
        ></div>
        <Container>
          {/* <div className="photo-container">
            <img alt="..." src={require("assets/img/ryan.jpg")}></img>
          </div> */}
          <h3 className="title">State</h3>
          {/* <Dropdown group isOpen={this.state.dropdownOpen} size="sm" toggle={this.toggle}> */}
          <UncontrolledDropdown nav>
            <DropdownToggle
              caret
              onClick={(e) => e.preventDefault()}
            >
              <i className="now-ui-icons design_app mr-1"></i>
              <p>Components</p>
            </DropdownToggle>
            <DropdownMenu>
              <DropdownItem to="/index">
                <i className="now-ui-icons business_chart-pie-36 mr-1"></i>
                All components
              </DropdownItem>
              <DropdownItem
                href="https://demos.creative-tim.com/now-ui-kit-react/#/documentation/introduction?ref=nukr-index-navbar"
                target="_blank"
              >
                <i className="now-ui-icons design_bullet-list-67 mr-1"></i>
                Documentation
              </DropdownItem>
            </DropdownMenu>
          </UncontrolledDropdown>
          <Dropdown>
            <DropdownToggle caret>
              State
            </DropdownToggle>
            <DropdownMenu>
              <DropdownItem>Another Action</DropdownItem>
            </DropdownMenu>
          </Dropdown>
          <p className="category">State Name</p>
          <div className="content">
            <div className="social-description">
              <p>number of parks</p>
              <h2>Parks</h2>
            </div>
            <div className="social-description">
              <p>number of species</p>
              <h2>Species</h2>
            </div>
          </div>
        </Container>
      </div>
    </>
  );
}

export default ProfilePageHeader;
